document.addEventListener("DOMContentLoaded",(function(event){var inputs=document.querySelector("#alphasearch");inputs.addEventListener("keydown",e=>{13==e.which&&firstClick()});var showButton=document.querySelector("#show"),resetButton=document.querySelector("#reset");function resetHandler(){inputs.setAttribute("placeholder","Search this page..."),turnOutTheLights()}function firstClick(){var blasted_items;document.getElementsByClassName("blast").length>0&&turnOutTheLights();for(var givenValue,givenValues=inputs.value.replace(/[!"#$%&()*+.\/:;<=>?@\[\\\]^`{|}~]/g,"").replace(/ /g,"").split(","),i=0;i<givenValues.length;i++){var alpha=givenValues[i];""!=alpha?blasterMaster(alpha):inputs.setAttribute("placeholder","Need Input...")}}function blasterMaster(term){for(var targets=document.getElementsByTagName("cell"),i=0;i<targets.length;i++)targets[i].innerHTML=targets[i].innerHTML.replace(new RegExp(term,"gi"),"<span class='blast'>$&</span>");blasted()}function blasted(){var pieces=document.getElementsByClassName("blast").length;if(0===pieces)inputs.value="",inputs.setAttribute("placeholder","No matches.");else if(pieces>0){var reeses=pieces+" matches.";inputs.value="",inputs.setAttribute("placeholder",reeses),setTheRayToJerry()}}function setTheRayToJerry(){blasted_items=document.getElementsByClassName("blast");for(var i=0;i<blasted_items.length;i++)blasted_items[i].parentElement.closest("td").setAttribute("class","alpha"),showButton.setAttribute("value","loaded")}function turnOutTheLights(){everything=document.querySelectorAll("*");for(var i=0;i<everything.length;i++)everything[i].classList.remove("blast","alpha")}showButton.addEventListener("click",firstClick),resetButton.addEventListener("click",resetHandler)}));