document.addEventListener("DOMContentLoaded",(function(){var inputs=document.querySelector("#alphasearch");inputs.addEventListener("keydown",e=>{if(13==e.which)return firstClick(),!1});var showButton=document.querySelector("#show"),resetButton=document.querySelector("#reset");function resetHandler(){return inputs.setAttribute("placeholder","Search this page..."),turnOutTheLights(),!1}function firstClick(){var blasted_items;document.getElementsByClassName("blast").length>0&&turnOutTheLights();for(var givenValue,givenValues=inputs.value.replace(/[!"#$%&()*+.\/:;<=>?@\[\\\]^`{|}~]/g,"").replace(/ /g,"").split(","),i=0;i<givenValues.length;i++){var alpha=givenValues[i];""!=alpha?blasterMaster(alpha):inputs.setAttribute("placeholder","Need Input...")}return!1}function blasterMaster(term){for(var targets=document.getElementsByTagName("cell"),i=0;i<targets.length;i++)targets[i].innerHTML=targets[i].innerHTML.replace(new RegExp(term,"gi"),"<span class='blast'>$&</span>");return blasted(),!1}function blasted(){var pieces=document.getElementsByClassName("blast").length;if(0===pieces)inputs.value="",inputs.setAttribute("placeholder","No matches."),shakingThrough();else if(pieces>0){var reeses=pieces+" matches.";inputs.value="",inputs.setAttribute("placeholder",reeses),setTheRayToJerry()}return!1}function shakingThrough(){return document.querySelector("#alphasearch").classList.add("horizontal-shake"),setTimeout((function(){document.querySelector("#alphasearch").classList.remove("horizontal-shake")}),1e3),!1}function setTheRayToJerry(){blasted_items=document.getElementsByClassName("blast");for(var i=0;i<blasted_items.length;i++)blasted_items[i].parentElement.closest("td").setAttribute("class","alpha"),showButton.setAttribute("value","loaded");return!1}function turnOutTheLights(){everything=document.querySelectorAll("*");for(var i=0;i<everything.length;i++)everything[i].classList.remove("blast","alpha");return!1}showButton.addEventListener("click",firstClick),resetButton.addEventListener("click",resetHandler)}));